import { Component } from '@angular/core';
import { TypeEnum } from '../utils/enums/TypeEnums';

export interface Dvd {
  name: String,
  type: TypeEnum,
  quantity: number,
  //price: bigint,
  //description: String,
  //image: String;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  // Fake Bdd
  dvds : Array<Dvd>  = [
    {
      name: 'Le roi lion',
      type: TypeEnum.KIDS,
      quantity: 10
    },
    {
      name: 'Armagedon',
      type: TypeEnum.AVENTURE,
      quantity: 2
    },

    {
      name: 'Coeur love',
      type: TypeEnum.ROMANCE,
      quantity: 5
    },
  ]

dvdToShow : Array<Dvd> = []

constructor(){}

handleTypeClickButton = (type : String) => {
  this.dvdToShow = this.dvds.filter((value) => {
    return value.type === type
  })
}

ngOnInit(){
  this.dvdToShow = this.dvds
}

}
